import React, {Component} from 'react';
import './Start.css';


export default class Start extends Component {
  constructor(props) {
    super(props);
      this.state= {
        startInputValue: ''
      }
    }

    selectCamping = (props) => {
      this.checkCampingLocation(props);
      return this.props.campgrounds;
    }

    selectHiking = (props) => {
      this.checkHikingLocation();
       return this.props.allTrails
    }

    grabLocation = (event) => {
      event.preventDefault();
      this.setState({
        startInputValue: event.target.value
      })
      console.log('test',this.state.startInputValue.toUpperCase());
    }

    checkCampingLocation = (props) => {
      console.log('test 1', this.props.allCampgrounds.location.toUpperCase())
      console.log('test 2',this.props.allCampgrounds['nearest-city'].toUpperCase())

      if(this.state.startInputValue.toUpperCase() === this.props.allCampgrounds.location.toUpperCase() || this.state.startInputValue.toUpperCase() === this.props.allCampgrounds['nearest-city'].toUpperCase()){
        console.log("Katie")
      }
    }

    // checkHikingLocation = (props) => {

    // }


  render () {
    return (
      <section className='startWrapper'>
        <h1 className="header">XCO</h1>
        <div className='buttonHolder'>
          <input type="button" className="campStartBtn"  value="Camping" onClick={this.selectCamping} />
          <input type="button" className="hikeStartBtn" value="Hiking" onClick={this.selectHiking} />
        </div>
        <form onSubmit={this.grabLocation} className=' startLocationSearch'>
          <input type="text" className="startLocationInput" placeholder="Please Enter a Location..." value= {this.props.startInputValue} onChange={this.grabLocation} />
          <input type="submit" className="startSubmitBtn" value="Submit" />
        </form>
      </section>
    )
  }
}
