import React, {Component} from 'react';
import './Start.css';


export default class Start extends Component {
  constructor(props) {
    super(props);
      this.state= {
        startInputValue: '',
        filteredCampgrounds: [],
        filteredTrails: []
        // hovered: false
      }
    }
    // onClick(props) {
    //   console.log('THIS', this)
    //   this.selectCampingBtn(props);
    //   // passCampLocation();
    //   this.selectHikingBtn(props);
    //   // passHikeLocation();
    // }

    selectCampingBtn = (props) => {
      this.checkCampingLocation(props);
      return this.props.campgrounds;
    }

    selectHikingBtn = (props) => {
      this.checkHikingLocation(props);
       return this.props.allTrails
    }

    grabLocation = (event) => {
      event.preventDefault();
      this.setState({
        startInputValue: event.target.value
      })
    }

    checkCampingLocation = (props) => {
      let filteredCampground = this.props.allCampgrounds.filter(campground => {
        return campground.location === this.state.startInputValue
      })
      this.setState({
        filteredCampgrounds: filteredCampground
      })
    }

    checkHikingLocation = (props) => {
      let filteredTrails = this.props.allTrails.filter(trail => {
        return trail.location === this.state.startInputValue
      }) 
        this.setState({
          filteredTrails: filteredTrails
      })
    }

  //   cancelHover = () => {
  //       this.setState ({
  //         hovered: false
  //       })
  //   }

  //   setHover = () => {
  //     this.setState ({
  //       hovered: true
  //     })
  // }


  render () {
    console.log(this.props.startInput );
    return (
      <section className='startWrapper'>
        <h1 className="header">XCO</h1>
        <section className='startLocationSearch'>
          <input type="text" className="startLocationInput" placeholder="Please Enter a Location..." value= {this.state.startInputValue} onChange={this.grabLocation} />
          <div className='buttonHolder'>
          <input type="button" className="campStartBtn"  value='Camping' onClick={(event) => { this.selectCampingBtn(); }} onMouseOver={this.setHover} onMouseOut={this.cancelHover} />
          <input type="button" className="hikeStartBtn" value='Hiking' onClick={(event) => { this.selectHikingBtn(); }} onMouseOver={this.setHover} onMouseOut={this.cancelHover} />
        </div>
        </section>
      </section>
    )
  }
}
